import randomRACE_END = 70tortoise = 1hare = 1timer = 0print('ON YOUR MARK, GET SET')print('BANG !!!!!')print("AND THEY'RE OFF !!!!!")def move_tortoise(tortoise):    move = random.randrange(1, 11)     # determine moves by percent    if move in (1,5):  # fast plod        tortoise += 3    elif move in (6, 7):  # slip        tortoise -= 6    else:  # slow plod        tortoise += 1    # ensure tortoise doesn't slip beyond start position or past the finish    if tortoise < 1:        tortoise = 1    elif tortoise > RACE_END:         tortoise = RACE_END    return tortoisedef move_hare(hare):    move = random.randrange(1, 11)  # randomize move to choose          # determine moves by percent    if move in (3, 4):  # big hop        hare += 9    elif move == 5:  # big slip        hare -= 12    elif move in (6, 8):  # small hop        hare += 1    elif move > 8:  # small slip        hare -= 2    # ensure hare doesn't slip beyond start position or past the finish    if hare < 1:        hare = 1    elif hare > RACE_END:         hare = RACE_END    return haredef print_positions(tortoise, hare):    for count in range(1, RACE_END + 1):        # tortoise and hare positions collide        if count == tortoise and count == hare:            print('OUCH!!!', end='')        elif count == hare:            print('H', end='')        elif count == tortoise:            print('T', end='')        else:            print(' ', end='')            print()# run the racewhile tortoise < RACE_END and hare < RACE_END:     hare = move_hare(hare)    tortoise = move_tortoise(tortoise)    print_positions(tortoise, hare)    timer += 1# tortoise beats hare or a tieif tortoise > hare:    print('\nTORTOISE WINS!!! YAY!!!')elif tortoise < hare:    print('\nHare wins. Yuch!')elif tortoise == hare:    print('\nIt is a tie')print(f'TIME ELAPSED = {timer} seconds')